import React, { useState, useEffect } from 'react';
import { Wallet, ShoppingCart, Package, LogIn, LogOut, Plus, History } from 'lucide-react';

const BlockchainMarketplace = () => {
  const [account, setAccount] = useState('');
  const [balance, setBalance] = useState(0);
  const [isRegistered, setIsRegistered] = useState(false);
  const [items, setItems] = useState([]);
  const [activeTab, setActiveTab] = useState('marketplace');
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);

  const [newItem, setNewItem] = useState({ name: '', description: '', price: '' });
  const [privateKey, setPrivateKey] = useState('');

  // Load items khi app kh·ªüi ƒë·ªông
  useEffect(() => {
    const savedItems = localStorage.getItem('marketplace_items');
    if (savedItems) {
      setItems(JSON.parse(savedItems));
    }
  }, []);

  // Load user data khi account thay ƒë·ªïi
  useEffect(() => {
    if (account) {
      loadUserData(account);
    }
  }, [account]);

  // L∆∞u items m·ªói khi thay ƒë·ªïi
  useEffect(() => {
    if (items.length > 0) {
      localStorage.setItem('marketplace_items', JSON.stringify(items));
    }
  }, [items]);

  const loadUserData = (userAccount) => {
    const userData = localStorage.getItem(`user_${userAccount}`);
    if (userData) {
      const data = JSON.parse(userData);
      setBalance(data.balance || 0);
      setIsRegistered(data.isRegistered || false);
      setTransactions(data.transactions || []);
    }
  };

  const saveUserData = (userAccount, newBalance, isReg, txs) => {
    const userData = {
      balance: newBalance,
      isRegistered: isReg,
      transactions: txs
    };
    localStorage.setItem(`user_${userAccount}`, JSON.stringify(userData));
  };

  const connectWallet = async () => {
    setLoading(true);
    setTimeout(() => {
      const mockAddress = '0x' + Math.random().toString(16).substr(2, 40);
      setAccount(mockAddress);
      setLoading(false);
    }, 500);
  };

  const loginWithKey = () => {
    if (privateKey.length < 10) {
      alert('Private key qu√° ng·∫Øn! T·ªëi thi·ªÉu 10 k√Ω t·ª±');
      return;
    }
    setLoading(true);
    setTimeout(() => {
      const mockAddress = '0x' + privateKey.substr(0, 40).padEnd(40, '0');
      setAccount(mockAddress);
      setPrivateKey('');
      setLoading(false);
    }, 500);
  };

  const registerUser = () => {
    setLoading(true);
    setTimeout(() => {
      const initialBalance = 1000;
      const newTransaction = {
        id: Date.now(),
        type: 'register',
        amount: initialBalance,
        description: 'Bonus ƒëƒÉng k√Ω t√†i kho·∫£n',
        timestamp: new Date().toISOString(),
        from: 'System',
        to: account
      };

      setIsRegistered(true);
      setBalance(initialBalance);
      setTransactions([newTransaction]);

      saveUserData(account, initialBalance, true, [newTransaction]);
      setLoading(false);
      alert('ƒêƒÉng k√Ω th√†nh c√¥ng! B·∫°n ƒë∆∞·ª£c c·∫•p 1000 ETH');
    }, 500);
  };

  const listItem = () => {
    if (!newItem.name || !newItem.price) {
      alert('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin!');
      return;
    }

    setLoading(true);
    setTimeout(() => {
      const item = {
        id: Date.now(),
        name: newItem.name,
        description: newItem.description,
        price: parseFloat(newItem.price),
        seller: account,
        sold: false,
        createdAt: new Date().toISOString()
      };

      const newItems = [...items, item];
      setItems(newItems);

      const newTransaction = {
        id: Date.now(),
        type: 'list',
        itemName: item.name,
        price: item.price,
        description: `ƒêƒÉng b√°n ${item.name}`,
        timestamp: new Date().toISOString(),
        from: account,
        to: 'Marketplace'
      };

      const updatedTransactions = [...transactions, newTransaction];
      setTransactions(updatedTransactions);
      saveUserData(account, balance, isRegistered, updatedTransactions);

      setNewItem({ name: '', description: '', price: '' });
      setLoading(false);
      alert('ƒêƒÉng b√°n th√†nh c√¥ng!');
    }, 500);
  };

  const purchaseItem = (item) => {
    if (balance < item.price) {
      alert('S·ªë d∆∞ kh√¥ng ƒë·ªß!');
      return;
    }

    if (item.seller === account) {
      alert('B·∫°n kh√¥ng th·ªÉ mua v·∫≠t ph·∫©m c·ªßa ch√≠nh m√¨nh!');
      return;
    }

    setLoading(true);
    setTimeout(() => {
      // C·∫≠p nh·∫≠t s·ªë d∆∞ ng∆∞·ªùi mua
      const newBuyerBalance = balance - item.price;
      setBalance(newBuyerBalance);

      // T·∫°o giao d·ªãch ng∆∞·ªùi mua
      const buyerTransaction = {
        id: Date.now(),
        type: 'purchase',
        itemName: item.name,
        amount: -item.price,
        description: `Mua ${item.name} t·ª´ ${item.seller.substring(0, 10)}...`,
        timestamp: new Date().toISOString(),
        from: account,
        to: item.seller
      };

      const updatedBuyerTransactions = [...transactions, buyerTransaction];
      setTransactions(updatedBuyerTransactions);
      saveUserData(account, newBuyerBalance, isRegistered, updatedBuyerTransactions);

      // C·∫≠p nh·∫≠t s·ªë d∆∞ ng∆∞·ªùi b√°n
      const sellerData = localStorage.getItem(`user_${item.seller}`);
      if (sellerData) {
        const seller = JSON.parse(sellerData);
        const newSellerBalance = (seller.balance || 1000) + item.price;

        const sellerTransaction = {
          id: Date.now() + 1,
          type: 'sale',
          itemName: item.name,
          amount: item.price,
          description: `B√°n ${item.name} cho ${account.substring(0, 10)}...`,
          timestamp: new Date().toISOString(),
          from: account,
          to: item.seller
        };

        const updatedSellerTransactions = [...(seller.transactions || []), sellerTransaction];
        saveUserData(item.seller, newSellerBalance, seller.isRegistered, updatedSellerTransactions);
      }

      // C·∫≠p nh·∫≠t tr·∫°ng th√°i item
      const updatedItems = items.map(i => 
        i.id === item.id ? { ...i, sold: true, buyer: account, soldAt: new Date().toISOString() } : i
      );
      setItems(updatedItems);

      setLoading(false);
      alert(`Mua th√†nh c√¥ng ${item.name}!`);
    }, 500);
  };

  const logout = () => {
    setAccount('');
    setBalance(0);
    setIsRegistered(false);
    setPrivateKey('');
    setTransactions([]);
    setActiveTab('marketplace');
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getTransactionIcon = (type) => {
    switch(type) {
      case 'register': return 'üéÅ';
      case 'purchase': return 'üõí';
      case 'sale': return 'üí∞';
      case 'list': return 'üì¶';
      default: return 'üí≥';
    }
  };

  const getTransactionTypeText = (type) => {
    switch(type) {
      case 'register': return 'ƒêƒÉng k√Ω';
      case 'purchase': return 'Mua h√†ng';
      case 'sale': return 'B√°n h√†ng';
      case 'list': return 'ƒêƒÉng b√°n';
      default: return 'Giao d·ªãch';
    }
  };

  // Styles gi·ªØ nguy√™n nh∆∞ b·∫£n c≈©
  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #EFF6FF 0%, #E0E7FF 100%)',
    },
    header: {
      background: 'white',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
    },
    headerContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '16px 24px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      flexWrap: 'wrap',
      gap: '16px',
    },
    logoSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
    },
    title: {
      fontSize: '24px',
      fontWeight: 'bold',
      color: '#4F46E5',
      margin: 0,
    },
    userSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '16px',
      flexWrap: 'wrap',
    },
    balanceBox: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      background: '#EEF2FF',
      padding: '8px 16px',
      borderRadius: '8px',
    },
    balanceText: {
      fontWeight: '600',
      color: '#4F46E5',
    },
    addressText: {
      fontSize: '14px',
      color: '#6B7280',
      fontFamily: 'monospace',
    },
    button: {
      padding: '8px 16px',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      fontWeight: '600',
      transition: 'all 0.3s',
      fontSize: '14px',
    },
    buttonPrimary: {
      background: '#4F46E5',
      color: 'white',
    },
    buttonDanger: {
      background: '#EF4444',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      gap: '4px',
    },
    mainContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '32px 16px',
    },
    tabs: {
      display: 'flex',
      gap: '8px',
      marginBottom: '24px',
      flexWrap: 'wrap',
    },
    tab: {
      padding: '12px 24px',
      border: 'none',
      borderRadius: '8px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
    },
    tabActive: {
      background: '#4F46E5',
      color: 'white',
    },
    tabInactive: {
      background: 'white',
      color: '#6B7280',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
      gap: '24px',
    },
    card: {
      background: 'white',
      borderRadius: '16px',
      boxShadow: '0 10px 40px rgba(0,0,0,0.1)',
      padding: '40px',
    },
    transactionCard: {
      background: 'white',
      borderRadius: '12px',
      padding: '20px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      marginBottom: '16px',
      transition: 'all 0.3s',
    },
    transactionHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      marginBottom: '12px',
    },
    transactionLeft: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
    },
    transactionIcon: {
      fontSize: '32px',
    },
    transactionInfo: {
      display: 'flex',
      flexDirection: 'column',
      gap: '4px',
    },
    transactionType: {
      fontSize: '16px',
      fontWeight: '600',
      color: '#1F2937',
    },
    transactionDesc: {
      fontSize: '14px',
      color: '#6B7280',
    },
    transactionAmount: {
      fontSize: '20px',
      fontWeight: 'bold',
      textAlign: 'right',
    },
    amountPositive: {
      color: '#10B981',
    },
    amountNegative: {
      color: '#EF4444',
    },
    transactionTime: {
      fontSize: '12px',
      color: '#9CA3AF',
      marginTop: '8px',
    },
    emptyState: {
      textAlign: 'center',
      padding: '60px 20px',
      color: '#9CA3AF',
    },
  };

  // Ph·∫ßn render gi·ªØ nguy√™n c·∫•u tr√∫c login/register...
  // Ch·ªâ th√™m tab History

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <div style={styles.headerContent}>
          <div style={styles.logoSection}>
            <Package size={32} color="#4F46E5" />
            <h1 style={styles.title}>Blockchain Marketplace</h1>
          </div>

          {account ? (
            <div style={styles.userSection}>
              <div style={styles.balanceBox}>
                <Wallet size={20} color="#4F46E5" />
                <span style={styles.balanceText}>{balance.toFixed(2)} ETH</span>
              </div>
              <div style={styles.addressText}>
                {account.substring(0, 6)}...{account.substring(38)}
              </div>
              <button
                onClick={logout}
                style={{...styles.button, ...styles.buttonDanger}}
                disabled={loading}
              >
                <LogOut size={16} />
                <span>ƒêƒÉng xu·∫•t</span>
              </button>
            </div>
          ) : (
            <button
              onClick={connectWallet}
              style={{...styles.button, ...styles.buttonPrimary}}
              disabled={loading}
            >
              {loading ? 'ƒêang k·∫øt n·ªëi...' : 'K·∫øt n·ªëi Wallet'}
            </button>
          )}
        </div>
      </header>

      <div style={styles.mainContent}>
        {!account ? (
          <div style={{maxWidth: '450px', margin: '80px auto'}}>
            <div style={styles.card}>
              <div style={{textAlign: 'center', marginBottom: '24px'}}>
                <LogIn size={64} color="#4F46E5" style={{margin: '0 auto 16px'}} />
                <h2 style={{fontSize: '24px', fontWeight: 'bold', color: '#1F2937', marginBottom: '8px'}}>ƒêƒÉng nh·∫≠p</h2>
                <p style={{color: '#6B7280'}}>S·ª≠ d·ª•ng private key ƒë·ªÉ ƒëƒÉng nh·∫≠p</p>
              </div>

              <div style={{display: 'flex', flexDirection: 'column', gap: '16px'}}>
                <div>
                  <label style={{fontSize: '14px', fontWeight: '600', color: '#374151', marginBottom: '8px', display: 'block'}}>
                    Private Key
                  </label>
                  <input
                    type="password"
                    value={privateKey}
                    onChange={(e) => setPrivateKey(e.target.value)}
                    placeholder="Nh·∫≠p private key (t·ªëi thi·ªÉu 10 k√Ω t·ª±)"
                    style={{width: '100%', padding: '12px 16px', border: '2px solid #E5E7EB', borderRadius: '8px', fontSize: '16px'}}
                  />
                </div>

                <button
                  onClick={loginWithKey}
                  style={{width: '100%', padding: '14px', background: '#4F46E5', color: 'white', border: 'none', borderRadius: '8px', fontSize: '16px', fontWeight: '600', cursor: 'pointer'}}
                  disabled={loading}
                >
                  {loading ? 'ƒêang x·ª≠ l√Ω...' : 'ƒêƒÉng nh·∫≠p'}
                </button>

                <div style={{textAlign: 'center', color: '#9CA3AF'}}>ho·∫∑c</div>

                <button
                  onClick={connectWallet}
                  style={{width: '100%', padding: '14px', background: '#F3F4F6', color: '#1F2937', border: '2px solid #E5E7EB', borderRadius: '8px', fontSize: '16px', fontWeight: '600', cursor: 'pointer'}}
                  disabled={loading}
                >
                  {loading ? 'ƒêang x·ª≠ l√Ω...' : 'T·∫°o t√†i kho·∫£n m·ªõi'}
                </button>
              </div>
            </div>
          </div>
        ) : !isRegistered ? (
          <div style={{maxWidth: '450px', margin: '80px auto'}}>
            <div style={styles.card}>
              <div style={{textAlign: 'center'}}>
                <Wallet size={64} color="#4F46E5" style={{margin: '0 auto 16px'}} />
                <h2 style={{fontSize: '24px', fontWeight: 'bold', color: '#1F2937', marginBottom: '8px'}}>Ch√†o m·ª´ng!</h2>
                <p style={{color: '#6B7280', marginBottom: '16px'}}>
                  ƒê·ªãa ch·ªâ v√≠ c·ªßa b·∫°n: <br />
                  <span style={{fontFamily: 'monospace', fontSize: '14px'}}>{account}</span>
