import React, { useState, useEffect } from 'react';
import { Wallet, ShoppingCart, Package, LogIn, LogOut, Plus } from 'lucide-react';

const BlockchainMarketplace = () => {
  const [account, setAccount] = useState('');
  const [balance, setBalance] = useState(0);
  const [isRegistered, setIsRegistered] = useState(false);
  const [items, setItems] = useState([]);
  const [activeTab, setActiveTab] = useState('marketplace');

  // Form states
  const [newItem, setNewItem] = useState({ name: '', description: '', price: '' });
  const [privateKey, setPrivateKey] = useState('');

  // Mock data for demonstration
  const mockItems = [
    { id: 1, name: 'Laptop Gaming', description: 'RTX 3080, i9 12900K', price: 50, seller: '0x123...', sold: false },
    { id: 2, name: 'iPhone 15 Pro', description: '256GB, Titan Blue', price: 30, seller: '0x456...', sold: false },
    { id: 3, name: 'PlayStation 5', description: 'Slim version with 2 controllers', price: 25, seller: '0x789...', sold: false },
    { id: 4, name: 'MacBook Air M2', description: '16GB RAM, 512GB SSD', price: 45, seller: '0xabc...', sold: true },
  ];

  useEffect(() => {
    setItems(mockItems);
  }, []);

  const connectWallet = async () => {
    // Simulation: In real app, use Web3.js
    const mockAddress = '0x' + Math.random().toString(16).substr(2, 40);
    setAccount(mockAddress);
    setIsRegistered(false);
  };

  const loginWithKey = () => {
    if (privateKey.length < 10) {
      alert('Private key quá ngắn!');
      return;
    }
    const mockAddress = '0x' + privateKey.substr(0, 40);
    setAccount(mockAddress);
    setIsRegistered(false);
    setPrivateKey('');
  };

  const registerUser = () => {
    setIsRegistered(true);
    setBalance(1000);
    alert('Đăng ký thành công! Bạn được cấp 1000 tokens');
  };

  const listItem = () => {
    if (!newItem.name || !newItem.price) {
      alert('Vui lòng điền đầy đủ thông tin!');
      return;
    }

    const item = {
      id: items.length + 1,
      name: newItem.name,
      description: newItem.description,
      price: parseFloat(newItem.price),
      seller: account,
      sold: false
    };

    setItems([...items, item]);
    setNewItem({ name: '', description: '', price: '' });
    alert('Đăng bán thành công!');
  };

  const purchaseItem = (item) => {
    if (balance < item.price) {
      alert('Số dư không đủ!');
      return;
    }

    if (item.seller === account) {
      alert('Bạn không thể mua vật phẩm của chính mình!');
      return;
    }

    setBalance(balance - item.price);
    const updatedItems = items.map(i => 
      i.id === item.id ? { ...i, sold: true, buyer: account } : i
    );
    setItems(updatedItems);
    alert(`Mua thành công ${item.name}!`);
  };

  const logout = () => {
    setAccount('');
    setBalance(0);
    setIsRegistered(false);
    setPrivateKey('');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <header className="bg-white shadow-md">
        <div className="container mx-auto px-4 py-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-2">
              <Package className="w-8 h-8 text-indigo-600" />
              <h1 className="text-2xl font-bold text-gray-800">Blockchain Marketplace</h1>
            </div>

            {account ? (
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2 bg-indigo-50 px-4 py-2 rounded-lg">
                  <Wallet className="w-5 h-5 text-indigo-600" />
                  <span className="font-semibold text-indigo-600">{balance} ETH</span>
                </div>
                <div className="text-sm text-gray-600">
                  {account.substring(0, 6)}...{account.substring(38)}
                </div>
                <button
                  onClick={logout}
                  className="flex items-center space-x-1 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition"
                >
                  <LogOut className="w-4 h-4" />
                  <span>Đăng xuất</span>
                </button>
              </div>
            ) : (
              <div className="flex space-x-2">
                <button
                  onClick={connectWallet}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
                >
                  Kết nối Wallet
                </button>
              </div>
            )}
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-8">
        {!account ? (
          /* Login Screen */
          <div className="max-w-md mx-auto mt-20">
            <div className="bg-white rounded-xl shadow-lg p-8">
              <div className="text-center mb-6">
                <LogIn className="w-16 h-16 text-indigo-600 mx-auto mb-4" />
                <h2 className="text-2xl font-bold text-gray-800">Đăng nhập</h2>
                <p className="text-gray-600 mt-2">Sử dụng private key để đăng nhập</p>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Private Key
                  </label>
                  <input
                    type="password"
                    value={privateKey}
                    onChange={(e) => setPrivateKey(e.target.value)}
                    placeholder="Nhập private key của bạn"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  />
                </div>

                <button
                  onClick={loginWithKey}
                  className="w-full py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition font-semibold"
                >
                  Đăng nhập
                </button>

                <div className="text-center text-sm text-gray-600">
                  hoặc
                </div>

                <button
                  onClick={connectWallet}
                  className="w-full py-3 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition font-semibold"
                >
                  Tạo tài khoản mới
                </button>
              </div>
            </div>
          </div>
        ) : !isRegistered ? (
          /* Registration Screen */
          <div className="max-w-md mx-auto mt-20">
            <div className="bg-white rounded-xl shadow-lg p-8 text-center">
              <Wallet className="w-16 h-16 text-indigo-600 mx-auto mb-4" />
              <h2 className="text-2xl font-bold text-gray-800 mb-2">Chào mừng!</h2>
              <p className="text-gray-600 mb-6">
                Địa chỉ ví của bạn: <br />
                <span className="font-mono text-sm">{account}</span>
              </p>
              <p className="text-gray-600 mb-6">
                Đăng ký để nhận 1000 tokens miễn phí và bắt đầu mua bán
              </p>
              <button
                onClick={registerUser}
                className="w-full py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition font-semibold"
              >
                Đăng ký ngay
              </button>
            </div>
          </div>
        ) : (
          /* Main App */
          <div>
            {/* Tabs */}
            <div className="flex space-x-2 mb-6">
              <button
                onClick={() => setActiveTab('marketplace')}
                className={`px-6 py-3 rounded-lg font-semibold transition ${
                  activeTab === 'marketplace'
                    ? 'bg-indigo-600 text-white'
                    : 'bg-white text-gray-600 hover:bg-gray-50'
                }`}
              >
                <ShoppingCart className="w-5 h-5 inline mr-2" />
                Chợ
              </button>
              <button
                onClick={() => setActiveTab('sell')}
                className={`px-6 py-3 rounded-lg font-semibold transition ${
                  activeTab === 'sell'
                    ? 'bg-indigo-600 text-white'
                    : 'bg-white text-gray-600 hover:bg-gray-50'
                }`}
              >
                <Plus className="w-5 h-5 inline mr-2" />
                Đăng bán
              </button>
            </div>

            {activeTab === 'marketplace' ? (
              /* Marketplace */
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {items.filter(item => !item.sold).map((item) => (
                  <div key={item.id} className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-lg transition">
                    <div className="h-48 bg-gradient-to-br from-indigo-400 to-purple-500 flex items-center justify-center">
                      <Package className="w-20 h-20 text-white opacity-50" />
                    </div>
                    <div className="p-6">
                      <h3 className="text-xl font-bold text-gray-800 mb-2">{item.name}</h3>
                      <p className="text-gray-600 mb-4 text-sm">{item.description}</p>
                      <div className="flex justify-between items-center mb-4">
                        <span className="text-2xl font-bold text-indigo-600">{item.price} ETH</span>
                        <span className="text-xs text-gray-500">
                          Người bán: {item.seller.substring(0, 8)}...
                        </span>
                      </div>
                      <button
                        onClick={() => purchaseItem(item)}
                        disabled={item.seller === account}
                        className={`w-full py-2 rounded-lg font-semibold transition ${
                          item.seller === account
                            ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                            : 'bg-indigo-600 text-white hover:bg-indigo-700'
                        }`}
                      >
                        {item.seller === account ? 'Vật phẩm của bạn' : 'Mua ngay'}
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              /* Sell Form */
              <div className="max-w-2xl mx-auto">
                <div className="bg-white rounded-xl shadow-lg p-8">
                  <h2 className="text-2xl font-bold text-gray-800 mb-6">Đăng bán vật phẩm</h2>

                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Tên vật phẩm
                      </label>
                      <input
                        type="text"
                        value={newItem.name}
                        onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
                        placeholder="VD: Laptop Gaming"
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Mô tả
                      </label>
                      <textarea
                        value={newItem.description}
                        onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
                        placeholder="Mô tả chi tiết về vật phẩm"
                        rows={4}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Giá (ETH)
                      </label>
                      <input
                        type="number"
                        value={newItem.price}
                        onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
                        placeholder="0.0"
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                      />
                    </div>

                    <button
                      onClick={listItem}
                      className="w-full py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition font-semibold"
                    >
                      Đăng bán
                    </button>
                  </div>
                </div>

                {/* My Items */}
                <div className="mt-8">
                  <h3 className="text-xl font-bold text-gray-800 mb-4">Vật phẩm của tôi</h3>
                  <div className="space-y-4">
                    {items.filter(item => item.seller === account).map((item) => (
                      <div key={item.id} className="bg-white rounded-lg shadow p-4 flex justify-between items-center">
                        <div>
                          <h4 className="font-semibold text-gray-800">{item.name}</h4>
                          <p className="text-sm text-gray-600">{item.description}</p>
                        </div>
                        <div className="text-right">
                          <div className="text-lg font-bold text-indigo-600">{item.price} ETH</div>
                          <div className={`text-sm ${item.sold ? 'text-green-600' : 'text-gray-500'}`}>
                            {item.sold ? 'Đã bán' : 'Đang bán'}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default BlockchainMarketplace;
