import React, { useState, useEffect } from 'react';
import { Wallet, ShoppingCart, Package, LogIn, LogOut, Plus } from 'lucide-react';

const BlockchainMarketplace = () => {
  const [account, setAccount] = useState('');
  const [balance, setBalance] = useState(0);
  const [isRegistered, setIsRegistered] = useState(false);
  const [items, setItems] = useState([]);
  const [activeTab, setActiveTab] = useState('marketplace');

  const [newItem, setNewItem] = useState({ name: '', description: '', price: '' });
  const [privateKey, setPrivateKey] = useState('');

  const mockItems = [
    { id: 1, name: 'Laptop Gaming', description: 'RTX 3080, i9 12900K', price: 50, seller: '0x123...', sold: false },
    { id: 2, name: 'iPhone 15 Pro', description: '256GB, Titan Blue', price: 30, seller: '0x456...', sold: false },
    { id: 3, name: 'PlayStation 5', description: 'Slim version with 2 controllers', price: 25, seller: '0x789...', sold: false },
    { id: 4, name: 'MacBook Air M2', description: '16GB RAM, 512GB SSD', price: 45, seller: '0xabc...', sold: true },
  ];

  useEffect(() => {
    setItems(mockItems);
  }, []);

  const connectWallet = async () => {
    const mockAddress = '0x' + Math.random().toString(16).substr(2, 40);
    setAccount(mockAddress);
    setIsRegistered(false);
  };

  const loginWithKey = () => {
    if (privateKey.length < 10) {
      alert('Private key quá ngắn!');
      return;
    }
    const mockAddress = '0x' + privateKey.substr(0, 40);
    setAccount(mockAddress);
    setIsRegistered(false);
    setPrivateKey('');
  };

  const registerUser = () => {
    setIsRegistered(true);
    setBalance(1000);
    alert('Đăng ký thành công! Bạn được cấp 1000 tokens');
  };

  const listItem = () => {
    if (!newItem.name || !newItem.price) {
      alert('Vui lòng điền đầy đủ thông tin!');
      return;
    }

    const item = {
      id: items.length + 1,
      name: newItem.name,
      description: newItem.description,
      price: parseFloat(newItem.price),
      seller: account,
      sold: false
    };

    setItems([...items, item]);
    setNewItem({ name: '', description: '', price: '' });
    alert('Đăng bán thành công!');
  };

  const purchaseItem = (item) => {
    if (balance < item.price) {
      alert('Số dư không đủ!');
      return;
    }

    if (item.seller === account) {
      alert('Bạn không thể mua vật phẩm của chính mình!');
      return;
    }

    setBalance(balance - item.price);
    const updatedItems = items.map(i => 
      i.id === item.id ? { ...i, sold: true, buyer: account } : i
    );
    setItems(updatedItems);
    alert(`Mua thành công ${item.name}!`);
  };

  const logout = () => {
    setAccount('');
    setBalance(0);
    setIsRegistered(false);
    setPrivateKey('');
  };

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #EFF6FF 0%, #E0E7FF 100%)',
    },
    header: {
      background: 'white',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    },
    headerContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '16px 24px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      flexWrap: 'wrap',
      gap: '16px',
    },
    logoSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
    },
    title: {
      fontSize: '24px',
      fontWeight: 'bold',
      color: '#4F46E5',
      margin: 0,
    },
    userSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '16px',
      flexWrap: 'wrap',
    },
    balanceBox: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      background: '#EEF2FF',
      padding: '8px 16px',
      borderRadius: '8px',
    },
    balanceText: {
      fontWeight: '600',
      color: '#4F46E5',
    },
    addressText: {
      fontSize: '14px',
      color: '#6B7280',
    },
    button: {
      padding: '8px 16px',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      fontWeight: '600',
      transition: 'all 0.3s',
      fontSize: '14px',
    },
    buttonPrimary: {
      background: '#4F46E5',
      color: 'white',
    },
    buttonDanger: {
      background: '#EF4444',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      gap: '4px',
    },
    mainContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '32px 16px',
    },
    loginContainer: {
      maxWidth: '450px',
      margin: '80px auto',
    },
    card: {
      background: 'white',
      borderRadius: '16px',
      boxShadow: '0 10px 40px rgba(0,0,0,0.1)',
      padding: '40px',
    },
    cardHeader: {
      textAlign: 'center',
      marginBottom: '24px',
    },
    cardTitle: {
      fontSize: '24px',
      fontWeight: 'bold',
      color: '#1F2937',
      marginBottom: '8px',
    },
    cardSubtitle: {
      color: '#6B7280',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      gap: '16px',
    },
    formGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '8px',
    },
    label: {
      fontSize: '14px',
      fontWeight: '600',
      color: '#374151',
    },
    input: {
      padding: '12px 16px',
      border: '2px solid #E5E7EB',
      borderRadius: '8px',
      fontSize: '16px',
      outline: 'none',
    },
    textarea: {
      padding: '12px 16px',
      border: '2px solid #E5E7EB',
      borderRadius: '8px',
      fontSize: '16px',
      outline: 'none',
      resize: 'vertical',
      fontFamily: 'inherit',
    },
    buttonFull: {
      width: '100%',
      padding: '14px',
      border: 'none',
      borderRadius: '8px',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s',
    },
    buttonSecondary: {
      background: '#F3F4F6',
      color: '#1F2937',
      border: '2px solid #E5E7EB',
    },
    divider: {
      textAlign: 'center',
      color: '#9CA3AF',
      position: 'relative',
      margin: '8px 0',
    },
    tabs: {
      display: 'flex',
      gap: '8px',
      marginBottom: '24px',
    },
    tab: {
      padding: '12px 24px',
      border: 'none',
      borderRadius: '8px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
    },
    tabActive: {
      background: '#4F46E5',
      color: 'white',
    },
    tabInactive: {
      background: 'white',
      color: '#6B7280',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
      gap: '24px',
    },
    itemCard: {
      background: 'white',
      borderRadius: '12px',
      overflow: 'hidden',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      transition: 'all 0.3s',
    },
    itemImage: {
      height: '192px',
      background: 'linear-gradient(135deg, #818CF8 0%, #A78BFA 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    itemContent: {
      padding: '24px',
    },
    itemTitle: {
      fontSize: '20px',
      fontWeight: 'bold',
      color: '#1F2937',
      marginBottom: '8px',
    },
    itemDescription: {
      fontSize: '14px',
      color: '#6B7280',
      marginBottom: '16px',
      minHeight: '40px',
    },
    itemFooter: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '16px',
    },
    itemPrice: {
      fontSize: '24px',
      fontWeight: 'bold',
      color: '#4F46E5',
    },
    itemSeller: {
      fontSize: '12px',
      color: '#9CA3AF',
    },
    myItemsList: {
      display: 'flex',
      flexDirection: 'column',
      gap: '16px',
      marginTop: '24px',
    },
    myItem: {
      background: 'white',
      borderRadius: '8px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      padding: '16px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
    },
    myItemInfo: {
      flex: 1,
    },
    myItemTitle: {
      fontWeight: '600',
      color: '#1F2937',
      marginBottom: '4px',
    },
    myItemDesc: {
      fontSize: '14px',
      color: '#6B7280',
    },
    myItemStatus: {
      textAlign: 'right',
    },
    statusBadge: {
      fontSize: '14px',
      padding: '4px 12px',
      borderRadius: '12px',
      fontWeight: '600',
    },
    statusSold: {
      background: '#D1FAE5',
      color: '#065F46',
    },
    statusSelling: {
      background: '#FEF3C7',
      color: '#92400E',
    },
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <div style={styles.headerContent}>
          <div style={styles.logoSection}>
            <Package size={32} color="#4F46E5" />
            <h1 style={styles.title}>Blockchain Marketplace</h1>
          </div>

          {account ? (
            <div style={styles.userSection}>
              <div style={styles.balanceBox}>
                <Wallet size={20} color="#4F46E5" />
                <span style={styles.balanceText}>{balance} ETH</span>
              </div>
              <div style={styles.addressText}>
                {account.substring(0, 6)}...{account.substring(38)}
              </div>
              <button
                onClick={logout}
                style={{...styles.button, ...styles.buttonDanger}}
                onMouseOver={(e) => e.target.style.background = '#DC2626'}
                onMouseOut={(e) => e.target.style.background = '#EF4444'}
              >
                <LogOut size={16} />
                <span>Đăng xuất</span>
              </button>
            </div>
          ) : (
            <button
              onClick={connectWallet}
              style={{...styles.button, ...styles.buttonPrimary}}
              onMouseOver={(e) => e.target.style.background = '#4338CA'}
              onMouseOut={(e) => e.target.style.background = '#4F46E5'}
            >
              Kết nối Wallet
            </button>
          )}
        </div>
      </header>

      <div style={styles.mainContent}>
        {!account ? (
          <div style={styles.loginContainer}>
            <div style={styles.card}>
              <div style={styles.cardHeader}>
                <LogIn size={64} color="#4F46E5" style={{margin: '0 auto 16px'}} />
                <h2 style={styles.cardTitle}>Đăng nhập</h2>
                <p style={styles.cardSubtitle}>Sử dụng private key để đăng nhập</p>
              </div>

              <div style={styles.form}>
                <div style={styles.formGroup}>
                  <label style={styles.label}>Private Key</label>
                  <input
                    type="password"
                    value={privateKey}
                    onChange={(e) => setPrivateKey(e.target.value)}
                    placeholder="Nhập private key của bạn"
                    style={styles.input}
                  />
                </div>

                <button
                  onClick={loginWithKey}
                  style={{...styles.buttonFull, ...styles.buttonPrimary}}
                  onMouseOver={(e) => e.target.style.background = '#4338CA'}
                  onMouseOut={(e) => e.target.style.background = '#4F46E5'}
                >
                  Đăng nhập
                </button>

                <div style={styles.divider}>hoặc</div>

                <button
                  onClick={connectWallet}
                  style={{...styles.buttonFull, ...styles.buttonSecondary}}
                  onMouseOver={(e) => e.target.style.background = '#E5E7EB'}
                  onMouseOut={(e) => e.target.style.background = '#F3F4F6'}
                >
                  Tạo tài khoản mới
                </button>
              </div>
            </div>
          </div>
        ) : !isRegistered ? (
          <div style={styles.loginContainer}>
            <div style={styles.card}>
              <div style={styles.cardHeader}>
                <Wallet size={64} color="#4F46E5" style={{margin: '0 auto 16px'}} />
                <h2 style={styles.cardTitle}>Chào mừng!</h2>
                <p style={styles.cardSubtitle}>
                  Địa chỉ ví của bạn: <br />
                  <span style={{fontFamily: 'monospace', fontSize: '14px'}}>{account}</span>
                </p>
                <p style={{...styles.cardSubtitle, marginTop: '16px'}}>
                  Đăng ký để nhận 1000 tokens miễn phí và bắt đầu mua bán
                </p>
              </div>
              <button
                onClick={registerUser}
                style={{...styles.buttonFull, ...styles.buttonPrimary}}
                onMouseOver={(e) => e.target.style.background = '#4338CA'}
                onMouseOut={(e) => e.target.style.background = '#4F46E5'}
              >
                Đăng ký ngay
              </button>
            </div>
          </div>
        ) : (
          <div>
            <div style={styles.tabs}>
              <button
                onClick={() => setActiveTab('marketplace')}
                style={{...styles.tab, ...(activeTab === 'marketplace' ? styles.tabActive : styles.tabInactive)}}
              >
                <ShoppingCart size={20} />
                Chợ
              </button>
              <button
                onClick={() => setActiveTab('sell')}
                style={{...styles.tab, ...(activeTab === 'sell' ? styles.tabActive : styles.tabInactive)}}
              >
                <Plus size={20} />
                Đăng bán
              </button>
            </div>

            {activeTab === 'marketplace' ? (
              <div style={styles.grid}>
                {items.filter(item => !item.sold).map((item) => (
                  <div key={item.id} style={styles.itemCard}>
                    <div style={styles.itemImage}>
                      <Package size={80} color="white" style={{opacity: 0.5}} />
                    </div>
                    <div style={styles.itemContent}>
                      <h3 style={styles.itemTitle}>{item.name}</h3>
                      <p style={styles.itemDescription}>{item.description}</p>
                      <div style={styles.itemFooter}>
                        <span style={styles.itemPrice}>{item.price} ETH</span>
                        <span style={styles.itemSeller}>
                          Người bán: {item.seller.substring(0, 8)}...
                        </span>
                      </div>
                      <button
                        onClick={() => purchaseItem(item)}
                        disabled={item.seller === account}
                        style={{
                          ...styles.buttonFull,
                          ...(item.seller === account ? {background: '#D1D5DB', color: '#6B7280', cursor: 'not-allowed'} : styles.buttonPrimary)
                        }}
                      >
                        {item.seller === account ? 'Vật phẩm của bạn' : 'Mua ngay'}
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div style={{maxWidth: '800px', margin: '0 auto'}}>
                <div style={styles.card}>
                  <h2 style={{...styles.cardTitle, textAlign: 'left', marginBottom: '24px'}}>Đăng bán vật phẩm</h2>

                  <div style={styles.form}>
                    <div style={styles.formGroup}>
                      <label style={styles.label}>Tên vật phẩm</label>
                      <input
                        type="text"
                        value={newItem.name}
                        onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
                        placeholder="VD: Laptop Gaming"
                        style={styles.input}
                      />
                    </div>

                    <div style={styles.formGroup}>
                      <label style={styles.label}>Mô tả</label>
                      <textarea
                        value={newItem.description}
                        onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
                        placeholder="Mô tả chi tiết về vật phẩm"
                        rows={4}
                        style={styles.textarea}
                      />
                    </div>

                    <div style={styles.formGroup}>
                      <label style={styles.label}>Giá (ETH)</label>
                      <input
                        type="number"
                        value={newItem.price}
                        onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
                        placeholder="0.0"
                        style={styles.input}
                      />
                    </div>

                    <button
                      onClick={listItem}
                      style={{...styles.buttonFull, background: '#10B981', color: 'white'}}
                      onMouseOver={(e) => e.target.style.background = '#059669'}
                      onMouseOut={(e) => e.target.style.background = '#10B981'}
                    >
                      Đăng bán
                    </button>
                  </div>
                </div>

                <div style={styles.myItemsList}>
                  <h3 style={{fontSize: '20px', fontWeight: 'bold', color: '#1F2937'}}>Vật phẩm của tôi</h3>
                  {items.filter(item => item.seller === account).map((item) => (
                    <div key={item.id} style={styles.myItem}>
                      <div style={styles.myItemInfo}>
                        <h4 style={styles.myItemTitle}>{item.name}</h4>
                        <p style={styles.myItemDesc}>{item.description}</p>
                      </div>
                      <div style={styles.myItemStatus}>
                        <div style={{...styles.itemPrice, marginBottom: '8px'}}>{item.price} ETH</div>
                        <div style={{...styles.statusBadge, ...(item.sold ? styles.statusSold : styles.statusSelling)}}>
                          {item.sold ? 'Đã bán' : 'Đang bán'}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default BlockchainMarketplace;
